/* eslint-disable react/jsx-props-no-spreading */
import { React, useState} from 'react';
import styled from 'styled-components'
import {
  Button,
  Form, Input, message
} from 'antd';
import { useHistory } from 'react-router-dom';
import 'antd/dist/antd.css';
import { connect } from 'react-redux';
import {authLogIn} from '../../../store/auth/middleware';
import { compareLSItemsAndDBItems } from '../../../store/wishlist/middleware';
import {setRefreshTimer} from '../../../store/auth/actionCreator'

const AuthForm = connect(null, {
  authLogIn,
  setRefreshTimer,
  compareLSItemsAndDBItems
})((
  {
    authLogIn,
    compareLSItemsAndDBItems,
    setRefreshTimer,
  }
) => {
  const startInterval = () => (
    setInterval(() => {
      authLogIn(JSON.parse(localStorage.getItem('credentials')))
    }, 1800000)
  )

  const formLayout = 'vertical'
  const [error, setError] = useState({})
  const history = useHistory()

  const onFinish = async (values) => {
    const {status, data} = await authLogIn(values)

    if (status === 200) {
      setRefreshTimer(startInterval())
      localStorage.setItem('token', data.token)
      history.push('/')
      compareLSItemsAndDBItems()
    }

    if (status === 400) {
      message.error(data.password)
      setError(data)
    }

    if (status === 404) {
      message.error(data.loginOrEmail)
      setError(data)
    }
  };

  const onChange = () => {
    setError({})
  }

  return (
    <Form
      name="authorization"
      layout={formLayout}
      initialValues={{
        remember: true,
      }}
      onFinish={onFinish}
      onChange={onChange}
    >
      <Form.Item
        label="Login or Email"
        name="loginOrEmail"
        {...error.loginOrEmail && {
          help: error.loginOrEmail,
          validateStatus: 'error',
        }}
        rules={[
          {
            required: true,
            message: 'Please input your login or email!',
          },
        ]}
      >
        <Input placeholder="Your Login or Email" size="large" />
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        {...error.password && {
          help: error.password,
          validateStatus: 'error',
        }}
        rules={[
          {
            required: true,
            message: 'Please input your password!',
          },
          {
            pattern: /^[a-zа0-9]+$/i,
            message: 'Allowed characters is a-z, 0-9'
          },
          {
            min: 8,
            message: 'Password length must be at least 8 symbols.',
          },
        ]}
      >
        <Input.Password placeholder="Your password" size="large" />
      </Form.Item>

      <Form.Item>
        <ButtonStyled type="primary" htmlType="submit" shape="round">
          Sign In
        </ButtonStyled>
      </Form.Item>
    </Form>
  );
});

const ButtonStyled = styled(Button)`
  width: 151px;
  height: 50px;
  background-color: rgba(1,86,255,1);
`

export default AuthForm
