import {SHOW_MODAL, HIDE_MODAL} from './actionType';

const initialState = {
  show: false,
  id: ''
}

export const reducer = (state = initialState, {type}) => {
  switch (type) {
    case SHOW_MODAL:
      return {
        ...state,
        show: true
      }
    case HIDE_MODAL:
      return {
        ...state,
        show: false
      }
    default:
      return state
  }
}
export default reducer;
