import React from 'react';
import { Descriptions } from 'antd';
import PropTypes from 'prop-types';
import ContainerOrder from './StyledOrderComponent'
// import upperCaseFirstLetter from '../../../utils/upperCaseFirstLetter';

const OrderComponent = ({orders}) => (
  <ContainerOrder>
    <br />
    <br />
    <Descriptions
      bordered
      // title={upperCaseFirstLetter(orders.name)}
      title={upperCaseFirstLetter(orders.name)}

      size="small"
    >
      <Descriptions.Item label="Номер заказа">{orders.brand}</Descriptions.Item>
      <Descriptions.Item label="Дата заказа">{orders.categories}</Descriptions.Item>
      <Descriptions.Item label="Тип оплаты">{orders.date.substr(0, 10)}</Descriptions.Item>
      <Descriptions.Item label="Кол-во товаров">{`${orders.currentPrice} $`}</Descriptions.Item>
      <Descriptions.Item label="Доставка">{orders.color}</Descriptions.Item>
      <Descriptions.Item label="Общая сумма">{orders.quantity}</Descriptions.Item>
      <Descriptions.Item label="Телефон">{orders.description}</Descriptions.Item>
    </Descriptions>
    <br />
    <br />
  </ContainerOrder>
)

OrderComponent.propTypes = {
  orders: PropTypes.instanceOf(Object).isRequired
}

export default OrderComponent;
