import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Circle, Amount } from './StyledCircleCount'
import { selectWishlistLength } from '../../../store/wishlist/reducer';

const mapStateToProps = (state) => ({
  wishlistLength: selectWishlistLength(state)
})

const CircleCount = connect(mapStateToProps, null)(({wishlistLength}) => (
  <Circle>
    <Amount>{wishlistLength === 0 ? (' ') : {wishlistLength}}</Amount>
  </Circle>
))

CircleCount.propTypes = {
  wishlistLength: PropTypes.number
}

export default CircleCount;
