import { SET_CUSTOMER_INFO } from './actionType';

export const MODULE_NAME = 'customerInfo';
export const selectCustomerInfo = (state) => state[MODULE_NAME].customerInfo

const initialState = {
  customerInfo: {}
}

export const reducer = (state = initialState, {type, payload}) => {
  switch (type) {
    case SET_CUSTOMER_INFO:
      return {
        ...state,
        customerInfo: payload
      }
    case UPDATE_CUSTOMER_INFO:
      return {
        ...state,
        customerInfo: {...state.customerInfo, ...payload}
      }
    default:
      return state
  }
}

export default reducer
