/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable jsx-a11y/no-static-element-interactions */
import React from 'react';
import {
  Descriptions, Dropdown, Button
} from 'antd';
import PropTypes from 'prop-types';
import {ContainerOrder, ShowListStyled, StyledMenu} from './StyledOrderComponent'

const OrderComponent = ({orders}) => {
  const menu = (
    <StyledMenu>
      {orders.products.map((item) => (
        <Menu.Item>
          <h6>{item.product.name}</h6>
          <h6>{item.cartQuantity}</h6>
        </Menu.Item>
      ))}
    </StyledMenu>
  );
  return (
    <ContainerOrder>
      <br />
      <br />
      <Descriptions
        bordered
      // title={upperCaseFirstLetter(orders.name)}
        title="Poka hz chto syda pistat`"
        size="small"
      >
        <Descriptions.Item label="Номер заказа">{orders.orderNo}</Descriptions.Item>
        <Descriptions.Item label="Дата заказа">{orders.date.substr(0, 10)}</Descriptions.Item>
        <Descriptions.Item label="Тип оплаты">{orders.paymentInfo}</Descriptions.Item>
        <Descriptions.Item label="Кол-во товаров">
          {orders.products.length}
          {' '}
          <ShowListStyled>
            <Dropdown overlay={menu} placement="bottomCenter" arrow>
              <Button>Show</Button>
            </Dropdown>
          </ShowListStyled>
        </Descriptions.Item>
        <Descriptions.Item label="Доставка">{orders.shipping}</Descriptions.Item>
        <Descriptions.Item label="Общая сумма">{orders.totalSum}</Descriptions.Item>
        <Descriptions.Item label="Телефон">{orders.mobile}</Descriptions.Item>
      </Descriptions>
      <br />
      <br />
    </ContainerOrder>
  );
}

OrderComponent.propTypes = {
  orders: PropTypes.instanceOf(Object).isRequired
}

export default OrderComponent;
