import styled from 'styled-components';
import { forTablet } from '../../../styles/mediaBreakPoints'

const Circle = styled.div`
    position: absolute;
    top: -2px;
    left: 32px;
    width: 15px;
    height: 15px;
    background-color: red;
    border-radius: 50%;
    @media(max-widht: ${forTablet.maxWidth}px) {
    left: 20px;
    }
`;
export default Circle;
