import React, {useState} from 'react';
import 'antd/dist/antd.css';
import {useCycle} from 'framer-motion';
import {
  DownOutlined
} from '@ant-design/icons';
import { Link } from 'react-router-dom';
import FaceBook from '../../images/header/FaceBook.png';
import Inst from '../../images/header/Inst.png';
import PopUpList from './PopUpList/PopUpList';
import PopUpShedulteContainer from './PopUpShadule/PopUpShedulteContainer';
import UserPopUp from './UserPopUp/UserPopUp';
import LogoMobile from './Utils/LogoMobile';
import LogoDesktop from './Utils/LogoDesktop'

// styled
import {
  HeaderContainer, ContainerAlign, ContactUsCall, CallBackAsk,
  SearchAndItemsBlock,
  RelativePosUserPopUp, CircleDesktop, ShaduleContainer,
  ShaduleArrowContainer, ShoppingCartOutlinedStyled,
  UserOutlinedStyled, SearchOutlinedStyledMedia,
  CircleMobile, MenuOutlinedStyled,
  CloseOutlinedFormStyled, TechTag,
} from './HeaderStyled';
import SearchProducts from './SearchProducts/SearchProducts';

const Header = () => {
  const [isOpen, setIsOpen] = useCycle(false, true);
  const [isOpenSheduleMenu, setisOpenSheduleMenu] = useState(false);
  const [isOpenUser, setIsOpenUser] = useState(false);
  const [hideInput, setHideInput] = useState(true);
  const [hideList, setHideList] = useState(false);

  const openSlide = {
    show: {
      clipPath: 'circle(2200px at 40px 40px)',
      transition: {
        type: 'spring',
        stiffness: 20,
        restDelta: 2
      }
    },
    hidden: {
      clipPath: 'circle(0.1px at 0.1px 0.1px)',
      transition: {
        type: 'spring',
        stiffness: 400,
        damping: 40
      }
    }
  };
  const checkForLinkOpen = (e) => {
    if (e.target.localName === 'h5') {
      setIsOpen((prev) => !prev)
    }
  }
  const openCloseMenu = () => {
    setIsOpen((prev) => (!prev))
  }
  const toggleShow = () => {
    setHideInput((prev) => !prev);
    setHideList((prev) => !prev);
  }

  return (
    <HeaderContainer>
      <ShaduleContainer>
        <ContainerAlign>
          <Link to="/" style={{outline: 'none'}}>
            <CircleMobile>
              <LogoMobile />
            </CircleMobile>
          </Link>
          <ShaduleArrowContainer id="shadule">
            <p>
              Mon - Thu:
              <span> 9.30 AM - 5.30 PM</span>
            </p>
            <div data-testid="arrowDownShedule" id="arrowDownShedule">
              <DownOutlined style={{color: 'white', cursor: 'pointer'}} />
            </div>
          </ShaduleArrowContainer>
          <ContactUsCall>
            <p>
              Visit our showroom in 1234 Street Adress City Address, 1234
            </p>
            <h3 style={{cursor: 'pointer'}}>Contact Us</h3>
          </ContactUsCall>
          <CallBackAsk>
            <h5>
              <a href="tel: +(00) 1234 5678" style={{ color: 'white' }}>
                Call Us: (00) 1234 5678
              </a>
            </h5>
            <div>
              <a href="https://www.facebook.com/" target="blank">
                <img src={FaceBook} alt="FaceBook" />
              </a>
            </div>
            <div>
              <a href="https://www.instagram.com/" target="blank">
                <img src={Inst} alt="Inst" />
              </a>
            </div>
          </CallBackAsk>
          <PopUpShedulteContainer
            setisOpenSheduleMenu={setisOpenSheduleMenu}
            isOpenSheduleMenu={isOpenSheduleMenu}
          />
        </ContainerAlign>
      </ShaduleContainer>
      <SearchAndItemsBlock>
        <MenuOutlinedStyled onClick={openCloseMenu} data-testid="burger" />
        <Link to="/" style={{outline: 'none', paddingRight: '20px'}}>
          <CircleDesktop>
            <LogoDesktop />
            <TechTag>Tech Store</TechTag>
          </CircleDesktop>
        </Link>
        <PopUpList
          checkForLinkOpen={checkForLinkOpen}
          hideList={hideList}
          setIsOpen={setIsOpen}
          openSlide={openSlide}
          isOpen={isOpen}
          openCloseMenu={openCloseMenu}
          hideInput={hideInput}
        />

        <SearchProducts hideInput={hideInput} setHideInput={setHideInput} />

        {hideInput
          ? (<SearchOutlinedStyledMedia onClick={toggleShow} />)
          : (<CloseOutlinedFormStyled onClick={toggleShow} />)}
        <WishListComponent />
        {/* <svg width={small ? 27 : 35}
        height={small ? 24 : 32} viewBox="0 0 17 14" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M7.68513 2.82536L8.39535
          3.54187L9.10557 2.82536C9.77879 2.14617 10.8139 1.375 11.8922 1.375C12.9606 1.375 13.8026 1.73075 14.3714 2.278C14.9374 2.82255 15.2936 3.61414 15.2936 4.60948C15.2936 5.68046 14.8643 6.59251 14.1287 7.47611C13.3739 8.3829 12.3563 9.19134 11.2509 10.0643L11.2252 10.0846C10.2883 10.8241 9.24578 11.647 8.39584 12.5675C7.55422 11.655 6.52157 10.8387 5.59303 10.1047L5.54239 10.0646L5.54198 10.0643C4.43628 9.19109 3.4189 8.38246 2.66433 7.47571C1.92905 6.59215 1.5 5.68023 1.5 4.60948C1.5 3.61414 1.8562 2.82257 2.42223 2.27804C2.99108 1.73079 3.83327 1.375 4.9021 1.375C5.97913 1.375 7.01114 2.14538 7.68513 2.82536Z" stroke="#dc0069" strokeWidth="1" />
        </svg> */}
        <Link to="/cart">
          <ShoppingCartOutlinedStyled />
        </Link>
        <RelativePosUserPopUp>
          <UserOutlinedStyled id="userBtn" data-testid="userBtn" />
          <UserPopUp
            isOpenUser={isOpenUser}
            setIsOpenUser={setIsOpenUser}
          />
        </RelativePosUserPopUp>
      </SearchAndItemsBlock>

    </HeaderContainer>
  );
};

export default Header;
