/* eslint-disable import/no-cycle */
/* eslint-disable react/prop-types */
/* eslint-disable no-unused-vars */
import React, {useEffect} from 'react';
import styled from 'styled-components'
import createNewSubscribe, { getSubscriber } from '../../../store/createSubscribe/middleware';
import { BlockInfo, TextInfo } from '../DashBoard';

const Subscribe = ({email, setSubscribedInfo}) => {
  useEffect(() => {
    const getSubscriberInfo = async () => {
      if (email) {
        const getCurrentResultOfSubscription = await getSubscriber(email);
        setSubscribedInfo(() => getCurrentResultOfSubscription)
      }
    }
    getSubscriberInfo()
  }, [email, setSubscribedInfo])

  return (
    <BlockInfo>
      <h4>Newsletters</h4>
      <div>
        <TextInfo>
          <p>
            You have been subscribed to newsupdates
          </p>
        </TextInfo>
      </div>
    </BlockInfo>

  );
}
export default Subscribe;
