/* eslint-disable prefer-promise-reject-errors */
/* eslint-disable react/jsx-props-no-spreading */
import React from 'react'
import { useHistory } from 'react-router-dom';
import { Input } from 'antd';
import StyledFrom from './StylesSignUpForm'
import { createCustomer } from '../../../../store/customer/middleware'
import StyledButton from '../../../common/Buttons/StyledButton'
import { validName, validLogin, validPassword } from '../../../../utils/constants'

const SignUpForm = () => {
  const history = useHistory()
  const onSubmit = (values) => {
    const credentials = Object.entries(values).reduce((acc, [key, value]) => {
      if (key !== 'confrimPassword' && value !== undefined) {
        acc[key] = value
        return acc
      }
      return acc
    }, {})
    credentials.isAdmin = true
    credentials.ratedProducts = []

    createCustomer(credentials, history)
  };

  const formLayout = {
    labelCol: {
      span: 10,
      offset: 0
    },
    wrapperCol: {
      span: 14,
    },

  }
  const tailLayout = {
    wrapperCol: { offset: 10 },
  };

  const passwordMatchValidator = ({ getFieldValue }) => {
    const isPasswordsMatch = (_, value) => {
      if (getFieldValue('password') !== value) {
        return Promise.reject('Passwords is not match.')
      }
      return Promise.resolve()
    }
    return {
      validator: isPasswordsMatch
    }
  }

  return (
    <StyledFrom
      {...formLayout}
      name="sign-up-form"
      initialValues={{
        remember: true,
      }}
      onFinish={onSubmit}
    >

      <StyledFrom.Item
        label="First name"
        name="firstName"
        rules={[
          {
            required: true,
            message: 'Please input your name.',
          },
          {
            pattern: validName,
            message: 'Allowed characters is a-z, а-я.'
          },
          {
            min: 2,
            max: 25,
            message: 'First Name must be beetwen 2 and 25 characters.'
          },
        ]}
      >
        <Input />
      </StyledFrom.Item>

      <StyledFrom.Item
        label="Last name"
        name="lastName"
        rules={[
          {
            required: true,
            message: 'Please input your last name.',
          },
          {
            pattern: validName,
            message: 'Allowed characters is a-z, а-я.'
          },
          {
            min: 2,
            max: 25,
            message: 'Last Name must be beetwen 2 and 25 characters.'
          },
        ]}
      >
        <Input />
      </StyledFrom.Item>

      <StyledFrom.Item
        label="Login"
        name="login"
        rules={[
          {
            required: true,
            message: 'Please set your login.',
          },
          {
            min: 3,
            max: 10,
            message: 'Login must be between 3 and 10 characters.',
          },
          {
            pattern: validLogin,
            message: 'Allowed characters is a-z, 0-9.'
          },
        ]}
      >
        <Input />
      </StyledFrom.Item>

      <StyledFrom.Item
        label="Email"
        name="email"
        rules={[
          {
            required: true,
            message: 'Please input your email.',
          },
          {
            type: 'email',
            message: 'Entered data is not an email.',
          },
        ]}
      >
        <Input />
      </StyledFrom.Item>

      <StyledFrom.Item
        label="Password"
        name="password"
        rules={[
          {
            required: true,
            message: 'Please input your password.',
          },
          {
            min: 8,
            max: 30,
            message: 'Password must be between 8 and 30 characters'
          },
          {
            pattern: validPassword,
            message: 'Allowed characters is a-z, 0-9.'
          },
        ]}
      >
        <Input.Password />
      </StyledFrom.Item>

      <StyledFrom.Item
        label="Confrim password"
        name="confrimPassword"
        rules={[
          {
            required: true,
            message: 'Please input your password.',
          },
          passwordMatchValidator,
        ]}
      >
        <Input.Password />
      </StyledFrom.Item>

      <StyledFrom.Item {...tailLayout}>
        <StyledButton size="lg" shape="round" htmlType="submit">Submit</StyledButton>
      </StyledFrom.Item>

    </StyledFrom>
  )
}

export default SignUpForm
