/* eslint-disable react/no-unescaped-entities */
/* eslint-disable no-unused-expressions */
/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import styled from 'styled-components'
import { connect } from 'react-redux';
import { forTablet, forDesktop, forMobile } from '../../styles/mediaBreakPoints';
import { Container } from '../common/Container'
import { getCustomer } from '../../store/customer/middleware'
import SpinAnimation from '../SpinAnimation/SpinAnimation'
import Modal from './DashBoardModal';
import FormInfoChange from './FormInfoChange';
import { setShowModal } from '../../store/dashBoardModal/middleware';
import createNewSubscribe, { getSubscriber } from '../../store/createSubscribe/middleware';
import { letterHtml, letterSubject } from '../Footer/footer-components/footer-config/letterConfig';

const DashBoard = connect(null, { getCustomer, setShowModal, createNewSubscribe })(({
  setShowModal,
  createNewSubscribe
}) => {
  const [info, setInfo] = useState({}) // customer

  const [subscribedInfo, setSubscribedInfo] = useState({}) // email

  useEffect(() => {
    const customer = async () => {
      const information = await getCustomer()
      const infoCustomer = information.data;
      setInfo(() => infoCustomer)
    }
    customer()
  }, [])

  useEffect(() => {
    const getSubscriberInfo = async () => {
      const {email} = info;
      if (email) {
        const getCurrentResultOfSubscription = await getSubscriber(email);
        setSubscribedInfo(() => getCurrentResultOfSubscription)
      }
    }
    getSubscriberInfo()
  }, [info])

  const addSubscriber = async (email, letterSubject, letterHtml) => {
    const result = await createNewSubscribe({email, letterSubject, letterHtml})
  }

  // eslint-disable-next-line consistent-return
  const ifSubscribed = (subscribedInfo, info) => {
    const {email} = info;
    console.log(subscribedInfo);
    if (subscribedInfo.data && subscribedInfo.data.enabled) {
      return (
        <p>
          You have been subscribed to newsupdates
        </p>
      )
    }
    return (
      <>
        <p>
          You don't subscribe to our newsletter.
        </p>
        <button
          type="submit"
          onClick={() => addSubscriber(email, letterSubject, letterHtml)}
        >
          Subscribe
        </button>
      </>

    )
  }
  if (Object.keys(info).length === 0) return <SpinAnimation width="90vw" height="90vh" />
  return (
    <Container>
      <MyDash>
        <p>My Dashboard</p>
      </MyDash>
      <AccountInfo>
        <h3>Account Information</h3>
      </AccountInfo>
      <WrapperBlocks>
        <RowBlocks>
          <BlockInfo>
            <h4>Contact Information</h4>
            <div>
              <TextInfo>
                <p>
                  {info.firstName}
                </p>
                <p>
                  {info.lastName}
                </p>
              </TextInfo>
              <div>
                <button type="submit" id="setInfo" onClick={(e) => setShowModal(e.target.id)}>Edit</button>
                <Modal setInfo={setInfo} title />
                <button type="submit" id="setPassword" onClick={(e) => setShowModal(e.target.id)}>Change Password</button>
              </div>
            </div>
          </BlockInfo>
        </RowBlocks>
        <RowBlocks>
          <BlockInfo>
            <h4>Newsletters</h4>
            <div>
              <TextInfo>
                {ifSubscribed(subscribedInfo, info)}
              </TextInfo>
            </div>
          </BlockInfo>
        </RowBlocks>
      </WrapperBlocks>
    </Container>
  );
})

const AccountInfo = styled.div`
  @media(max-width: ${forMobile.maxWidth}px) {
      width: 90%;
    }
  @media(min-width: ${forTablet.minWidth}px) {
     width: 70%;
     margin: 0 auto;
  }
  @media(min-width: ${forDesktop.minWidth}px) {
     width: 50%;
     margin: 0 auto;
  }
  h3{
    border-bottom: 1px solid #CCCCCC;
    padding: 0px 0px 10px 0px;
    @media(max-width: ${forMobile.maxWidth}px) {
      text-align: left;
    }
    @media(min-width: ${forTablet.minWidth}px) {
      color: #212121;
      font-size: 14px;
      text-align: center;
    }
  }
`;

const BtnMenage = styled.button`
  position: absolute;
  top: -50%;
  right: -5%;
`;
const MyDash = styled.aside`
  padding-top: 20px;
  padding-bottom: 15px;
  @media(min-width: ${forDesktop.minWidth}px) {
    padding-bottom: 2px;
  }
  p{ 
    font-size: 18px;
    font-weight: bold;
    @media(min-width: ${forDesktop.minWidth}px) {
    font-size: 25px;
  }
  }
`;
const WrapperBlocks = styled.div`
  display: flex;
  flex-direction: column;
  @media(min-width: ${forTablet.minWidth}px) {
    align-items: center;
    padding-bottom: 2px;
    border-top: 0px solid #CCCC;
  }
  button {
    border: none;
    background-color: transparent;
    color: #0156FF;
    text-decoration-line: underline;
    font-style: normal;
    font-weight: 300;
    margin-top: 10px;
  }
`;
const RowBlocks = styled.div`
  display: flex;
  flex-direction: column;
  @media(min-width: ${forTablet.minWidth}px) {
    flex-direction: row;
  }
`;
const BlockInfo = styled.div`
  padding: 20px 20px 20px 0px;

  @media(min-width: ${forTablet.minWidth}px) {
      padding: 20px 20px 20px 20px;
  }
  h4{
    font-weight: bold;
    padding-bottom: 15px;
    text-align: center;
  }
`;

const TextInfo = styled.div`
  p{
    padding: 5px;
  }
`;
export default DashBoard;
