/* eslint-disable import/no-cycle */
/* eslint-disable react/prop-types */
/* eslint-disable no-unused-vars */
import React, {useEffect} from 'react';
import styled from 'styled-components'
import { connect } from 'react-redux';
import createNewSubscribe, { getSubscriber } from '../../../store/createSubscribe/middleware';
import { BlockInfo, TextInfo } from '../DashBoard';

const Subscribe = connect(null, {createNewSubscribe})(({email, setSubscribedInfo, createNewSubscribe}) => {
  useEffect(() => {
    const getSubscriberInfo = async () => {
      if (email) {
        const getCurrentResultOfSubscription = await getSubscriber(email);
        setSubscribedInfo(() => getCurrentResultOfSubscription)
      }
    }
    getSubscriberInfo()
  }, [email, setSubscribedInfo])

  const addSubscriber = async (email, letterSubject, letterHtml) => {
    const result = await createNewSubscribe({email, letterSubject, letterHtml})
    if (!result && result.status !== 200) return
    console.log(result.data.subscriber.enabled);
  }

  return (
    <BlockInfo>
      <h4>Newsletters</h4>
      <div>
        <TextInfo>
          <p>
            You have been subscribed to newsupdates
          </p>
        </TextInfo>
      </div>
    </BlockInfo>

  );
})
export default Subscribe;
