import styled from 'styled-components';
import {
  Menu
} from 'antd';
import { forMobile, forTablet } from '../../../styles/mediaBreakPoints'

export const ContainerOrder = styled.div`
  border-top: 1px solid rgba(0,0,0,0.1);
  .ant-descriptions-item-label {
    padding: 8px 13px 0px 13px!important;
  }
`;
export const ShowListStyled = styled.span`
  text-decoration-line: underline;
  color: #0156FF;
  cursor: pointe;
`;
export const StyledMenu = styled(Menu)`
  @media(max-width: ${forMobile.maxWidth}px) {
     .ant-dropdown-menu-item, .ant-dropdown-menu-submenu-title {
      white-space: pre-wrap;
    } 
  }
  @media(max-width: ${forTablet.maxWidth}px) {
   .ant-dropdown-menu {
     margin: 0px 100px 0px 100px;
   }
  }
`;
